async function Select(command, editor, context) {
    console.log('[Spoken]: Executing: "select"');
    if (command.selectLine != undefined) {
        return await editor.select(parseInt(command.from, 10), parseInt(command.to, 10), true);
    }
    else if (command.word != undefined) {
        const pos = await editor.findPositionOf(command.word);
        if (pos.length) {
            return await editor.select(pos[0][0], pos[0][1] - 1, false);
        }
        throw new Error('Nothing found for string: ' + command.word);
    }
    const from = command.from || '';
    const to = command.to || '';
    let fromPos = parseInt(command.fromPosition, 10) || 1;
    let toPos = parseInt(command.toPosition, 10) || 1;
    if (from === '' || to === '') {
        throw new Error('Invalid arguments!');
    }
    const matchFrom = await editor.findPositionOf(from);
    const matchTo = await editor.findPositionOf(to);
    if (toPos === -1)
        toPos = matchTo.length;
    else if (fromPos === -1)
        fromPos = matchFrom.length;
    return await editor.select(matchFrom[fromPos - 1][0], matchTo[toPos - 1][0], false);
}
// @ts-ignore
return Select;

